import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { Author } from '../../models/Author';

interface AuthorFormProps {
  onSave: (author: Author) => void;
  initialData?: Author;
}

const AuthorForm: React.FC<AuthorFormProps> = ({ onSave, initialData }) => {
  const isNewAuthor = !initialData || !initialData.id;

  const validate = (values: Author) => {
    const errors: Record<string, string> = {};
    if (!values.name) {
      errors.name = 'Nome é obrigatório';
    }
    if (!values.email) {
      errors.email = 'Email é obrigatório';
    } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
      errors.email = 'Email inválido';
    }
    return errors;
  };

  const generateId = () => {
    return Math.floor(Math.random() * 999999) + 1;
  };

  return (
    <Formik
      initialValues={initialData || { id: '', name: '', email: '' }}
      validate={validate}
      onSubmit={(values, actions) => {
        const authorToSave = isNewAuthor ? { ...values, id: generateId().toString() } : values;
        onSave(authorToSave);
        actions.setSubmitting(false);
      }}
    >
      {({ isSubmitting }) => (
        <Form>
          <Field type="text" name="name" placeholder="Nome do Autor" />
          <ErrorMessage name="name" component="div" />

          <Field type="email" name="email" placeholder="Email do Autor" />
          <ErrorMessage name="email" component="div" />

          <button type="submit" disabled={isSubmitting}>
            Salvar
          </button>
        </Form>
      )}
    </Formik>
  );
};

export default AuthorForm;
