import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Modal from './components/Modal/Modal';
import AuthorForm from './components/AuthorForm/AuthorForm';
import BookForm from './components/BookForm/BookForm';
import { Author } from './models/Author'; 
import { Book } from './models/Book'; 
import useLocalStorage from './components/LocalStorage/useLocalStorage';
import BooksTable from './components/BooksTable/BooksTable';
import AuthorsTable from './components/AuthorsTable/AuthorsTable';
import Header from './pages/partials/Header/Header';
import Footer from './pages/partials/Footer/Footer';
import './App.css';
import AuthorsPage from './pages/AuthorsPage';
import BooksPage from './pages/BooksPage'; 

function App() {
  const [isAuthorModalOpen, setIsAuthorModalOpen] = useState(false);
  const [isBookModalOpen, setIsBookModalOpen] = useState(false);
  const [authors, setAuthors] = useLocalStorage<Author[]>('authors', []);
  const [books, setBooks] = useLocalStorage<Book[]>('books', []);
  const handleSaveAuthor = (author: Author) => {
    setAuthors([...authors, author]);
    setIsAuthorModalOpen(false);
  };

  const handleSaveBook = (book: Book) => {
    setBooks([...books, book]);
    setIsBookModalOpen(false);
  };

  return (
    <div className="App">
      <Header />
      <nav>
        <Link to="/authors">Autores</Link>
        <Link to="/books">Livros</Link>
      </nav>
      <Routes>
        <Router path="/authors" element={<AuthorsPage />} />
        <Router path="/books" element={<BooksPage />} />
      </Routes> 
      <button onClick={() => setIsAuthorModalOpen(true)}>Adicionar Autor</button>
      <button onClick={() => setIsBookModalOpen(true)}>Adicionar Livro</button>

      <Modal show={isAuthorModalOpen} onClose={() => setIsAuthorModalOpen(false)}>
        <AuthorForm onSave={handleSaveAuthor} />
      </Modal>

      <Modal show={isBookModalOpen} onClose={() => setIsBookModalOpen(false)}>
        <BookForm onSave={handleSaveBook} />
      </Modal>
      <BooksTable />
      <AuthorsTable />
      <Footer />
    </div>
  );
}

export default App;
