import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { Book } from '../../models/Book';

interface BookFormProps {
  onSave: (book: Book) => void;
  initialData?: Book;
}

const BookForm: React.FC<BookFormProps> = ({ onSave, initialData = { id: '', name: '', authorId: '', pages: 0 } }) => {

  const validate = (values: Book) => {
    const errors: Record<string, string> = {};
    if (!values.name) {
      errors.name = 'Nome é obrigatório';
    }
    if (!values.authorId) {
      errors.authorId = 'ID do Autor é obrigatório';
    }
    if (values.pages != null && values.pages < 0) {
      errors.pages = 'O número de páginas não pode ser negativo';
    }
    return errors;
  };

  return (
    <Formik
      initialValues={initialData}
      validate={validate}
      onSubmit={(values, actions) => {
        onSave({ ...initialData, ...values });
        actions.setSubmitting(false);
      }}
    >
      {({ isSubmitting }) => (
        <Form>
          <Field type="text" name="name" placeholder="Nome do Livro" />
          <ErrorMessage name="name" component="div" />

          <Field type="text" name="authorId" placeholder="ID do Autor" />
          <ErrorMessage name="authorId" component="div" />

          <Field type="number" name="pages" placeholder="Número de Páginas" />
          <ErrorMessage name="pages" component="div" />

          <button type="submit" disabled={isSubmitting}>
            Salvar
          </button>
        </Form>
      )}
    </Formik>
  );
};

export default BookForm;
