import AuthorForm from "./components/AuthorForm/AuthorForm";
import { Author } from './models/Author';
import useLocalStorage from "./components/LocalStorage";

export const App = () =>{

    // Inicializa o armazenamento local com um array vazio se ainda não houver dados
    const [authors, setAuthors] = useLocalStorage<Author[]>('authors', []);

    const handleSaveAuthor = (newAuthor: Author) => {
      // Adiciona o novo autor ao array existente ou atualiza um existente
      const updatedAuthors = [...authors];
      const index = updatedAuthors.findIndex(author => author.id === newAuthor.id);

      if (index !== -1) {
        updatedAuthors[index] = newAuthor; // Atualiza o autor existente
      } else {
        updatedAuthors.push(newAuthor); // Adiciona um novo autor
      }

      setAuthors(updatedAuthors); // Salva a lista atualizada no LocalStorage
      console.log('Autor salvo:', newAuthor);
    };

  return (
    <div className="App">
      <AuthorForm onSave={handleSaveAuthor} />
    </div>
  );
}
