import React, { useState } from 'react';
import * as Dialog from '@radix-ui/react-dialog';
import { Author } from '../../models/Author';
import useLocalStorage from '../LocalStorage/useLocalStorage';
import './AuthorsTable.css'; // Ajuste conforme necessário
import { TrashIcon, EyeOpenIcon } from '@radix-ui/react-icons';

const AuthorsTable = () => {
  const [authors, setAuthors] = useLocalStorage<Author[]>('authors', []);
  const [selectedAuthor, setSelectedAuthor] = useState<Author | null>(null);

  const handleDelete = (authorId: string) => {
    const updatedAuthors = authors.filter(author => author.id !== authorId);
    window.alert("Deseja realmente apagar?");
    setAuthors(updatedAuthors);
  };

  return (
    <div className="table-container">
      <table>
        <thead>
          <tr>
            <th>Nome do Autor</th>
            <th>Email</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {authors.map((author) => (
            <tr key={author.id}>
              <td>{author.name}</td>
              <td>{author.email || 'N/A'}</td>
              <td>
                <EyeOpenIcon className="details-icon" onClick={() => setSelectedAuthor(author)} />
                <TrashIcon className="delete-icon" onClick={() => { if (author.id) {handleDelete(author.id); } }} />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/*Dialogo de feedback para detalhes do autor */}
      <Dialog.Root open={selectedAuthor !== null} onOpenChange={() => setSelectedAuthor(null)}>
        <Dialog.Trigger asChild />
        <Dialog.Overlay className="dialog-overlay" />
        <Dialog.Content className="dialog-content">
          <Dialog.Title>Detalhes do Autor</Dialog.Title>
          {selectedAuthor && (
            <div>
              <p><strong>Nome:</strong> {selectedAuthor.name}</p>
              <p><strong>Email:</strong> {selectedAuthor.email}</p>
            </div>
          )}
          <Dialog.Close asChild>
            <button className="dialog-close">Fechar</button>
          </Dialog.Close>
        </Dialog.Content>
      </Dialog.Root>
    </div>
  );
};

export default AuthorsTable;
