import React, { useEffect, useState } from 'react';
import * as Dialog from '@radix-ui/react-dialog';
import { Author } from '../../models/Author'; // Ajuste conforme necessário
import { Book } from '../../models/Book'; // Ajuste conforme necessário
import useLocalStorage from '../LocalStorage/useLocalStorage'; 
import '../BookList/BookList.css'// Ajuste conforme necessário
import { TrashIcon } from '@radix-ui/react-icons';

const BooksList: React.FC = () => {
  const [books, setBooks] = useLocalStorage<Book[]>('books', []);
  const [authors] = useLocalStorage<Author[]>('authors', []);
  const [selectedBook, setSelectedBook] = useState<Book | null>(null);
  const [deleteCandidate, setDeleteCandidate] = useState<string | null>(null);

  interface BookWithAuthor extends Book {
    authorName: string;
  }

  const [booksWithAuthors, setBooksWithAuthors] = useState<BookWithAuthor[]>([]);

  useEffect(() => {
    const mergedBooks = books.map(book => {
      const author = authors.find(author => author.id === book.authorId);
      return { ...book, authorName: author ? author.name : 'Autor desconhecido' };
    });
    setBooksWithAuthors(mergedBooks);
  }, [books, authors]);

  const handleDeleteConfirm = () => {
    if (deleteCandidate) {
      const updatedBooks = books.filter(book => book.id !== deleteCandidate);
      setBooks(updatedBooks); // Atualiza o estado
    }
    setDeleteCandidate(null); // Fecha o diálogo de confirmação
  };

  return (
    <div className="table-container">
      <h2>Lista de Livros</h2>
      <table>
        <thead>
          <tr>
            <th>Nome do Livro</th>
            <th>Autor</th>
            <th>Número de Páginas</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {booksWithAuthors.map((book) => (
            <tr key={book.id}>
              <td>{book.name}</td>
              <td>{book.authorName}</td>
              <td>{book.pages || 'N/A'}</td>
              <td>
                <button onClick={() => setSelectedBook(book)}>Ver Livro</button>
                <TrashIcon className="delete-icon" onClick={() => setDeleteCandidate(book.id)} />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Diálogo de Confirmação para Exclusão */}
      <Dialog.Root open={deleteCandidate !== null} onOpenChange={() => setDeleteCandidate(null)}>
        <Dialog.Trigger />
        <Dialog.Overlay />
        <Dialog.Content>
          <Dialog.Title>Confirmação</Dialog.Title>
          <Dialog.Description>
            Tem certeza que deseja excluir este livro?
          </Dialog.Description>
          <button onClick={handleDeleteConfirm}>Sim</button>
          <Dialog.Close>Cancelar</Dialog.Close>
        </Dialog.Content>
      </Dialog.Root>

      {/* Modal para Visualizar Informações do Livro */}
      <Dialog.Root open={selectedBook !== null} onOpenChange={() => setSelectedBook(null)}>
        <Dialog.Trigger />
        <Dialog.Overlay />
        <Dialog.Content>
          <Dialog.Title>Informações do Livro</Dialog.Title>
          {selectedBook && (
            <div>
              <p>Nome: {selectedBook.name}</p>
              <p>Número de Páginas: {selectedBook.pages}</p>
            </div>
          )}
          <Dialog.Close>Fechar</Dialog.Close>
        </Dialog.Content>
      </Dialog.Root>
    </div>
  );
};

export default BooksList;
