import React from 'react';
import BookForm from './components/BookForm/BookForm';
import AuthorForm from './components/AuthorForm/AuthorForm';
import { Book } from './models/Book';
import { Author } from './models/Author';
import useLocalStorage from './components/LocalStorage/useLocalStorage';

function App() {
  const [books, setBooks] = useLocalStorage<Book[]>('books', []);
  const [authors, setAuthors] = useLocalStorage<Author[]>('authors', []);

  const handleSaveBook = (newBook: Book) => {
    const updatedBooks = books.map(book => book.id === newBook.id ? newBook : book);
    if (!updatedBooks.includes(newBook)) {
      newBook.id = generateUniqueId(books);
      updatedBooks.push(newBook);
    }
    setBooks(updatedBooks);
  };

  const handleSaveAuthor = (newAuthor: Author) => {
    const isUpdatingAuthor = newAuthor.id && authors.some(author => author.id === newAuthor.id);

    const updatedAuthors = isUpdatingAuthor 
      ? authors.map(author => author.id === newAuthor.id ? newAuthor : author)
      : [...authors, { ...newAuthor, id: generateUniqueId(authors) }];

    setAuthors(updatedAuthors);
  };

  const generateUniqueId = (items: { id: string }[]) => {
    let newId = '';
    do {
      newId = Math.floor(Math.random() * 999999).toString();
    } while (items.some(item => item.id === newId));
    return newId;
  };

  return (
    <div className="App">
      <AuthorForm onSave={handleSaveAuthor} />
      <BookForm onSave={handleSaveBook} />
    </div>
  );
}

export default App;
